window.onload = () => {
  const scrollChat = document.querySelector("#user-message");
  scrollChat.scrollTo(0, scrollChat.scrollHeight);
}

var lastMessage = document.querySelectorAll("#user-message p");

for (let i = 0; i < lastMessage.length; i++) {
  let k = lastMessage.length - 2;
  console.log(k);
  console.log(k.style.height);
}
