window.onload = () => {
  const scrollChat = document.querySelector("#user-message");
  scrollChat.scrollTo(0, scrollChat.scrollHeight);
}
var wrapperForMessages = document.querySelector('#user-message');
console.log(wrapperForMessages.style.height);
