var containerForMessageBody = document.createElement('p');
var wrapperForMessages = document.querySelector('#user-message');
var inputMessages = document.querySelector('#user-message-input p textarea');
var windowScroll = document.getElementById('user-message');

console.log(windowScroll);
alert(windowScroll);
window.onload = function () {
     var windowScroll = document.getElementById("user-message");
     windowScroll.scrollBottom = windowScroll.scrollHeight;
}
console.log(containerForMessageBody);
containerForMessageBody.innerHTML = `<%= @message.body %>`;
wrapperForMessages.appendChild(containerForMessageBody);
inputMessages.value = "";
